// src/pages/Annulation.tsx
import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { gql, useMutation } from '@apollo/client';

const DELETE_RESERVATION = gql`
  mutation DeleteReservation($id: ID!) {
    deleteReservation(id: $id) {
      ok
    }
  }
`;

const Annulation = () => {
  const { id, date } = useParams();
  const navigate = useNavigate();
  const [deleteReservation] = useMutation(DELETE_RESERVATION);

  // Sécurité : si pas d'id → erreur visuelle
  if (!id || !date) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-red-600 font-bold text-lg">
          Erreur : ID ou date manquante dans l’URL.
        </p>
      </div>
    );
  }

  const dateVoyage = new Date(date);
  const now = new Date();
  const diffHeures = (dateVoyage.getTime() - now.getTime()) / (1000 * 60 * 60);
  const remboursable = diffHeures >= 48;

  const handleConfirmAnnulation = async () => {
    try {
      await deleteReservation({ variables: { id } });
      navigate('/reservations');
    } catch (error) {
      console.error('Erreur lors de la suppression :', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <div className="bg-white p-8 rounded-xl shadow-md max-w-lg w-full text-center">
        {remboursable ? (
          <>
            <h2 className="text-xl font-bold text-green-600 mb-4">Remboursement en cours</h2>
            <p className="text-gray-700 mb-6">
              La réservation a été annulée avec succès.<br />
              Vous recevrez votre remboursement sur le moyen de paiement utilisé dans un délai de 3 jours ouvrables.
            </p>
            <button
              onClick={handleConfirmAnnulation}
              className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition"
            >
              Confirmer l’annulation
            </button>
          </>
        ) : (
          <>
            <h2 className="text-xl font-bold text-red-600 mb-4">Annulation sans remboursement</h2>
            <p className="text-gray-700 mb-6">
              Cette réservation sera annulée sans remboursement car elle est à moins de 48h du départ.<br />
              Voulez-vous vraiment l’annuler ?
            </p>
            <button
              onClick={handleConfirmAnnulation}
              className="bg-red-500 text-white px-6 py-2 rounded-md hover:bg-red-600 transition"
            >
              Oui, annuler sans remboursement
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Annulation;
