import React, { useState } from 'react';
import { gql, useMutation } from '@apollo/client';
import { useNavigate } from 'react-router-dom';

const CREATE_RESERVATION = gql`
  mutation CreateReservation($destination: String!) {
    createReservation(destination: $destination) {
      reservation {
        id
        destination
        statut
      }
    }
  }
`;

export default function ReservationForm() {
  const navigate = useNavigate();
  const [destination, setDestination] = useState('');
  const [createReservation, { loading, error }] = useMutation(CREATE_RESERVATION, {
    onCompleted() {
      setTimeout(() => {
        navigate('/reservations');
      }, 1500);
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!destination.trim()) return;
    createReservation({ variables: { destination } });
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-6 bg-white rounded-xl shadow-md space-y-6">
      <h1 className="text-3xl font-extrabold text-blue-900 mb-6 text-center">Réserver votre voyage</h1>

      <div>
        <label htmlFor="destination" className="block text-blue-800 font-semibold mb-1">Destination</label>
        <input
          type="text"
          id="destination"
          name="destination"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
          className="w-full border rounded-md p-3 text-lg border-gray-300"
          placeholder="Ex: Paris"
        />
      </div>

      <button
        type="submit"
        disabled={loading}
        className="w-full bg-yellow-400 hover:bg-yellow-500 transition-colors text-blue-900 font-bold py-3 rounded-xl shadow-md disabled:opacity-50"
      >
        {loading ? 'Réservation...' : 'Réserver'}
      </button>

      {error && <p className="text-red-600 text-center mt-2">Erreur : {error.message}</p>}
    </form>
  );
}
