import React, { useState } from 'react';

export default function ReservationForm() {
  const [formData, setFormData] = useState({
    nom: '',
    email: '',
    date: '',
    nombrePersonnes: 1,
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ici tu pourras gérer l'envoi des données (API, state, etc)
    console.log('Réservation soumise:', formData);
  };

  return (
    <form className="max-w-md mx-auto p-6 bg-white rounded shadow" onSubmit={handleSubmit}>
      <h2 className="text-2xl font-bold mb-4">Réservation</h2>

      <label className="block mb-2 font-semibold" htmlFor="nom">Nom</label>
      <input
        id="nom"
        name="nom"
        type="text"
        value={formData.nom}
        onChange={handleChange}
        className="w-full border border-gray-300 rounded px-3 py-2 mb-4"
        required
      />

      <label className="block mb-2 font-semibold" htmlFor="email">Email</label>
      <input
        id="email"
        name="email"
        type="email"
        value={formData.email}
        onChange={handleChange}
        className="w-full border border-gray-300 rounded px-3 py-2 mb-4"
        required
      />

      <label className="block mb-2 font-semibold" htmlFor="date">Date</label>
      <input
        id="date"
        name="date"
        type="date"
        value={formData.date}
        onChange={handleChange}
        className="w-full border border-gray-300 rounded px-3 py-2 mb-4"
        required
      />

      <label className="block mb-2 font-semibold" htmlFor="nombrePersonnes">Nombre de personnes</label>
      <input
        id="nombrePersonnes"
        name="nombrePersonnes"
        type="number"
        min="1"
        value={formData.nombrePersonnes}
        onChange={handleChange}
        className="w-full border border-gray-300 rounded px-3 py-2 mb-6"
        required
      />

      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
        Réserver
      </button>
    </form>
  );
}
