import React from "react";
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import { useParams } from "react-router-dom";

const stripePromise = loadStripe("pk_test_51R8CHoFaC4ABhVfvtcBNtizA9MT4Yl0DI6h3W3L3q0fW8nTqQsMPZqsabrr6imIXjPGzX88CngvGL7ismRjbXVpm001z2CWBTZ"); // ta cl√© publique

export default function Paiement() {
  const { id } = useParams();

  // üîç Test pour voir si l'ID est bien re√ßu
  console.log("ID r√©servation:", id);

  const handlePayer = async () => {
    try {
      const response = await fetch(`http://localhost:8000/create-checkout-session/${id}/`);
      const data = await response.json();

      if (data.url) {
        window.location.href = data.url; // redirection vers Stripe Checkout
      } else {
        alert("Erreur lors de la cr√©ation de la session de paiement");
      }
    } catch (error) {
      console.error("Erreur lors de l'appel √† Stripe:", error);
      alert("Impossible de contacter le serveur de paiement.");
    }
  };

  return (
    <Elements stripe={stripePromise}>
      <div className="flex flex-col items-center justify-center min-h-screen">
        <h1 className="text-3xl font-bold mb-4">Paiement de la r√©servation</h1>
        <button
          onClick={handlePayer}
          className="bg-blue-500 hover:bg-blue-600 text-white font-semibold px-4 py-2 rounded"
        >
          Payer
        </button>
      </div>
    </Elements>
  );
}
