import React, { useState } from 'react';
import { gql, useQuery } from '@apollo/client';
import { useParams } from 'react-router-dom';

const GET_RESERVATION = gql`
  query GetReservation($id: ID!) {
    reservation(id: $id) {
      id
      destination
      nom
      prenom
      email
      dateVoyage
      nombrePersonnes
      statut
    }
  }
`;

export default function ReservationDetail() {
  const { id } = useParams<{ id: string }>();
  const { data, loading, error } = useQuery(GET_RESERVATION, { variables: { id } });
  const [msg, setMsg] = useState<string | null>(null);

  const handleConfirm = async () => {
    try {
      const resp = await fetch(`http://192.168.2.15:8000/create-checkout-session/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ reservation_id: id }),
      });

      if (!resp.ok) {
        const text = await resp.text();
        throw new Error(text || 'Erreur serveur');
      }

      const { url } = await resp.json(); // backend renvoie { url: "https://checkout.stripe.com/..." }
      window.location.href = url;

    } catch (e: any) {
      console.error(e);
      setMsg(`Erreur : ${e.message}`);
    }
  };

  if (loading) return <p>Chargement…</p>;
  if (error) return <p>Erreur : {error.message}</p>;

  return (
    <div>
      <h1>Réservation pour {data.reservation.destination}</h1>
      {msg && <p className="text-red-600">{msg}</p>}
      <button
        onClick={handleConfirm}
        className="bg-yellow-400 px-4 py-2 rounded"
      >
        Confirmer et payer
      </button>
    </div>
  );
}
