import React, { useState } from 'react';
import { gql, useMutation } from '@apollo/client';
import { useNavigate } from 'react-router-dom';

const CREATE_RESERVATION = gql`
  mutation CreateReservation($input: ReservationInput!) {
    createReservation(input: $input) {
      reservation {
        id
        nom
        prenom
        email
        destination
        dateVoyage
        nombrePersonnes
        statut
      }
    }
  }
`;

export default function ReservationForm() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    destination: '',
    dateVoyage: '',
    nombrePersonnes: 1,
  });
  const [policyAccepted, setPolicyAccepted] = useState(false);
  const [errors, setErrors] = useState({});

  const [createReservation, { loading, error }] = useMutation(CREATE_RESERVATION, {
    onCompleted(data) {
      const id = data?.createReservation?.reservation?.id;
      if (id) navigate(`/reservation/${id}`);
    },
  });

  const validate = () => {
    const newErrors = {};
    if (!formData.nom.trim()) newErrors.nom = 'Le nom est obligatoire';
    if (!formData.prenom.trim()) newErrors.prenom = 'Le pr√©nom est obligatoire';
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) newErrors.email = "L'email est invalide";
    if (!formData.destination.trim()) newErrors.destination = 'La destination est obligatoire';
    if (!formData.dateVoyage) newErrors.dateVoyage = 'La date est obligatoire';
    if (Number(formData.nombrePersonnes) < 1) newErrors.nombrePersonnes = 'Minimum 1 personne';
    if (!policyAccepted) newErrors.policy = 'Vous devez accepter la politique pour continuer';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]:
        name === 'nombrePersonnes'
          ? Math.max(1, parseInt(value || '1', 10))
          : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;
    createReservation({ variables: { input: formData } });
  };

  const today = new Date().toISOString().slice(0, 10);

  return (
    <div className="min-h-screen bg-slate-50 flex items-center justify-center p-4">
      <form
        onSubmit={handleSubmit}
        className="w-full max-w-3xl bg-white rounded-2xl shadow-xl border border-slate-200 overflow-hidden"
      >
        {/* Header */}
        <div className="bg-blue-700 text-white px-6 py-5">
          <h1 className="text-2xl font-bold">‚úàÔ∏è R√©server un voyage</h1>
          <p className="text-blue-100 text-sm mt-1">
            Remplissez les informations ci-dessous pour cr√©er votre r√©servation.
          </p>
        </div>

        {/* Body */}
        <div className="p-6 md:p-8 space-y-6">
          {/* Grid inputs */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-5">
            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1">Nom</label>
              <input
                type="text"
                name="nom"
                value={formData.nom}
                onChange={handleChange}
                placeholder="Dupont"
                className={`w-full rounded-xl border px-3 py-2 focus:outline-none focus:ring-2 ${
                  errors.nom ? 'border-red-400 focus:ring-red-200' : 'border-slate-300 focus:ring-blue-200'
                }`}
              />
              {errors.nom && <p className="text-red-600 text-xs mt-1">{errors.nom}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1">Pr√©nom</label>
              <input
                type="text"
                name="prenom"
                value={formData.prenom}
                onChange={handleChange}
                placeholder="Marie"
                className={`w-full rounded-xl border px-3 py-2 focus:outline-none focus:ring-2 ${
                  errors.prenom ? 'border-red-400 focus:ring-red-200' : 'border-slate-300 focus:ring-blue-200'
                }`}
              />
              {errors.prenom && <p className="text-red-600 text-xs mt-1">{errors.prenom}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1">Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="exemple@mail.com"
                className={`w-full rounded-xl border px-3 py-2 focus:outline-none focus:ring-2 ${
                  errors.email ? 'border-red-400 focus:ring-red-200' : 'border-slate-300 focus:ring-blue-200'
                }`}
              />
              {errors.email && <p className="text-red-600 text-xs mt-1">{errors.email}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1">Destination</label>
              <input
                type="text"
                name="destination"
                value={formData.destination}
                onChange={handleChange}
                placeholder="Paris, New York‚Ä¶"
                className={`w-full rounded-xl border px-3 py-2 focus:outline-none focus:ring-2 ${
                  errors.destination ? 'border-red-400 focus:ring-red-200' : 'border-slate-300 focus:ring-blue-200'
                }`}
              />
              {errors.destination && <p className="text-red-600 text-xs mt-1">{errors.destination}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1">Date du voyage</label>
              <input
                type="date"
                name="dateVoyage"
                min={today}
                value={formData.dateVoyage}
                onChange={handleChange}
                className={`w-full rounded-xl border px-3 py-2 focus:outline-none focus:ring-2 ${
                  errors.dateVoyage ? 'border-red-400 focus:ring-red-200' : 'border-slate-300 focus:ring-blue-200'
                }`}
              />
              {errors.dateVoyage && <p className="text-red-600 text-xs mt-1">{errors.dateVoyage}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1">Nombre de personnes</label>
              <input
                type="number"
                name="nombrePersonnes"
                min={1}
                step={1}
                value={formData.nombrePersonnes}
                onChange={handleChange}
                onWheel={(e) => e.currentTarget.blur()}
                className={`w-full rounded-xl border px-3 py-2 focus:outline-none focus:ring-2 max-w-xs ${
