import React, { useEffect, useState } from 'react';
import { useQuery, useMutation, gql } from '@apollo/client';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { CheckCircleIcon, XCircleIcon, PencilSquareIcon } from '@heroicons/react/24/solid';

const GET_RESERVATIONS = gql`
  query {
    reservations {
      id
      nom
      prenom
      email
      destination
      dateVoyage
      nombrePersonnes
      statut
      dateReservation
    }
  }
`;

const UPDATE_RESERVATION = gql`
  mutation UpdateReservation($id: ID!, $statut: String) {
    updateReservation(id: $id, statut: $statut) {
      reservation { id statut }
    }
  }
`;

interface Reservation {
  id: string;
  nom: string;
  prenom: string;
  email: string;
  destination: string;
  dateVoyage: string;
  nombrePersonnes: number;
  statut: string;
  dateReservation: string;
}

const Reservations = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [banner, setBanner] = useState<string | null>(null);

  const { loading, error, data, refetch } = useQuery(GET_RESERVATIONS, {
    fetchPolicy: 'network-only',
  });

  const [updateReservation] = useMutation(UPDATE_RESERVATION);

  useEffect(() => {
    refetch();
  }, [refetch]);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    if (params.get('paid') === '1') {
      setBanner('✅ Paiement réussi : votre réservation a été mise à jour.');
      const url = new URL(window.location.href);
      url.searchParams.delete('paid');
      window.history.replaceState({}, '', url.toString());
    }
  }, [location.search]);

  const canModify = (dateISO: string) => {
    const depart = new Date(`${dateISO}T00:00:00`);
    const diffHours = (depart.getTime() - Date.now()) / (1000 * 60 * 60);
    return diffHours >= 72;
  };

  const showTooLate = () => {
    alert("⛔ Modification impossible : il reste moins de 72 heures avant le départ.");
  };

  if (loading) return <p className="text-center text-blue-700 mt-8">Chargement...</p>;
  if (error) return <p className="text-center text-red-600 mt-8">Erreur : {error.message}</p>;

  const reservations: Reservation[] = data?.reservations ?? [];

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white text-gray-800">
      {/* NavBar Premium */}
      <nav className="bg-white border-b border-gray-200 py-4 px-8 shadow-sm flex justify-between items-center">
        <h1 className="text-2xl font-extrabold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent flex items-center gap-2">
          🌍 CST Voyage
        </h1>
        <ul className="flex space-x-6 font-medium text-gray-700">
          <li><Link to="/informations" className="hover:text-blue-600">Mes informations</Link></li>
          <li><Link to="/reservations" className="text-blue-600 font-semibold">Mes réservations</Link></li>
          <li><Link to="/factures" className="hover:text-blue-600">Mes factures</Link></li>
        </ul>
      </nav>

      {/* Bandeau succès */}
      {banner && (
        <div className="max-w-4xl mx-auto mt-6 px-4">
          <div className="flex items-center justify-between rounded-lg border border-green-300 bg-green-50 text-green-800 px-4 py-3 shadow-sm">
            <span>{banner}</span>
            <button onClick={() => setBanner(null)} className="text-sm font-semibold hover:underline">Fermer</button>
          </div>
        </div>
      )}

      {/* Titre */}
      <h2 className="text-3xl font-bold text-center text-blue-900 my-10 tracking-wide">
        Mes réservations
      </h2>

      {/* Cartes */}
      <div className="space-y-8 max-w-4xl mx-auto px-4">
        {reservations.map((res: Reservation) => (
          <div
            key={res.id}
            className="bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-shadow duration-300 overflow-hidden"
          >
            {/* Bandeau destination */}
            <div className="bg-gradient-to-r from-blue-100 to-blue-50 px-6 py-4 flex justify-between items-center">
              <h3 className="text-xl font-bold text-blue-900 flex items-center gap-2">
                ✈️ {res.destination}
              </h3>
              {res.statut === 'CONFIRMEE' && (
                <span className="text-sm font-semibold px-4 py-1 rounded-full bg-yellow-200 text-yellow-800 flex items-center gap-1">
                  <CheckCircleIcon className="h-4 w-4" /> CONFIRMÉE
                </span>
              )}
              {res.statut === 'PAYEE' && (
                <span className="text-sm font-semibold px-4 py-1 rounded-full bg-green-200 text-green-800 flex items-center gap-1">
                  <CheckCircleIcon className="h-4 w-4" /> PAYÉE
                </span>
              )}
            </div>

            {/* Infos */}
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 px-6 py-4 text-base">
              <p><strong>Nom :</strong> {res.nom}</p>
              <p><strong>Prénom :</strong> {res.prenom}</p>
              <p><strong>Email :</strong> {res.email}</p>
              <p><strong>Date du voyage :</strong> {res.dateVoyage}</p>
              <p><strong>Nombre de personnes :</strong> {res.nombrePersonnes}</p>
              <p><strong>Date de réservation :</strong> {new Date(res.dateReservation).toLocaleString()}</p>
            </div>

            {/* Boutons */}
            <div className="flex justify-end gap-4 px-6 pb-6">
              {res.statut !== 'PAYEE' && (
                canModify(res.dateVoyage) ? (
                  <button
                    onClick={() => navigate(`/reservation-edit/${res.id}`)}
                    className="flex items-center gap-2 bg-gradient-to-r from-blue-500 to-blue-600 text-white px-5 py-2 rounded-lg shadow hover:from-blue-600 hover:to-blue-700 transition"
                  >
                    <PencilSquareIcon className="h-5 w-5" /> Modifier
                  </button>
                ) : (
                  <button
                    onClick={showTooLate}
                    className="flex items-center gap-2 bg-gray-200 text-gray-500 px-5 py-2 rounded-lg cursor-not-allowed"
                  >
                    <PencilSquareIcon className="h-5 w-5" /> Modifier
                  </button>
                )
              )}

              <Link
                to={`/annulation/${res.id}/${encodeURIComponent(res.dateVoyage)}`}
                className="flex items-center gap-2 bg-gradient-to-r from-red-400 to-red-500 text-white px-5 py-2 rounded-lg shadow hover:from-red-500 hover:to-red-600 transition"
              >
                <XCircleIcon className="h-5 w-5" /> Annuler
              </Link>
            </div>
          </div>
        ))}

        {reservations.length === 0 && (
          <div className="bg-white rounded-xl shadow p-6 text-center text-slate-500">
            Aucune réservation pour le moment.
          </div>
        )}
      </div>
    </div>
  );
};

export default Reservations;
