import React, { useEffect, useState } from 'react';

interface Facture {
  id: number;
  fichier_pdf: string;
  reservation_id: number;
  date_creation: string;
}

export default function Factures() {
  const [factures, setFactures] = useState<Facture[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/factures/') // 🔁 tu vas créer cette vue Django ensuite
      .then((res) => res.json())
      .then((data) => {
        setFactures(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Erreur lors du chargement des factures :", err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p className="text-center text-blue-700 mt-10">Chargement...</p>;

  if (factures.length === 0) {
    return <p className="text-center text-gray-500 mt-10">Aucune facture disponible.</p>;
  }

  return (
    <div className="min-h-screen bg-yellow-50 py-10 px-4">
      <h1 className="text-3xl font-bold text-center text-blue-900 mb-8">📄 Mes factures</h1>
      <div className="max-w-2xl mx-auto space-y-4">
        {factures.map((facture) => (
          <div
            key={facture.id}
            className="bg-white p-4 rounded-xl shadow flex justify-between items-center"
          >
            <div>
              <p className="text-blue-900 font-semibold">
                Réservation #{facture.reservation_id}
              </p>
              <p className="text-gray-500 text-sm">
                Générée le : {new Date(facture.date_creation).toLocaleString()}
              </p>
            </div>
            <a
              href={`http://127.0.0.1:8000/media/${facture.fichier_pdf}`}
              target="_blank"
              rel="noopener noreferrer"
              className="text-white bg-blue-600 px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Voir PDF
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}
