import React from 'react';
import { useQuery, gql } from '@apollo/client';

const GET_RESERVATIONS = gql`
  query {
    reservations {
      id
      destination
      statut
      dateReservation
    }
  }
`;

const Reservations = () => {
  const { loading, error, data } = useQuery(GET_RESERVATIONS);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p>Erreur : {error.message}</p>;

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4 text-blue-600">Mes r√©servations</h2>
      <ul className="space-y-2">
        {data.reservations.map((res: any) => (
          <li key={res.id} className="bg-white shadow p-4 rounded">
            <p><strong>Destination :</strong> {res.destination}</p>
            <p><strong>Statut :</strong> {res.statut}</p>
            <p><strong>Date :</strong> {new Date(res.dateReservation).toLocaleString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Reservations;
