import React, { useEffect, useState } from 'react';
import { useQuery, useMutation, gql } from '@apollo/client';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const GET_RESERVATIONS = gql`
  query {
    reservations {
      id
      nom
      prenom
      email
      destination
      dateVoyage
      nombrePersonnes
      statut
      dateReservation
    }
  }
`;

const UPDATE_RESERVATION = gql`
  mutation UpdateReservation($id: ID!, $statut: String) {
    updateReservation(id: $id, statut: $statut) {
      reservation {
        id
        statut
      }
    }
  }
`;

const DELETE_RESERVATION = gql`
  mutation DeleteReservation($id: ID!) {
    deleteReservation(id: $id) {
      ok
    }
  }
`;

interface Reservation {
  id: string;
  nom: string;
  prenom: string;
  email: string;
  destination: string;
  dateVoyage: string;           // YYYY-MM-DD
  nombrePersonnes: number;
  statut: string;
  dateReservation: string;      // ISO
}

const Reservations = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [banner, setBanner] = useState<string | null>(null);

  // 👇 force le réseau au chargement (puis revient en cache-first)
  const { loading, error, data, refetch } = useQuery(GET_RESERVATIONS, {
    fetchPolicy: 'network-only',
    nextFetchPolicy: 'cache-first',
  });

  const [updateReservation] = useMutation(UPDATE_RESERVATION);
  const [deleteReservation] = useMutation(DELETE_RESERVATION);

  // 👇 ceinture + bretelles : relance un refetch au montage de la page
  useEffect(() => {
    refetch();
  }, [refetch]);

  // 👇 bandeau succès après retour Stripe (?paid=1)
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    if (params.get('paid') === '1') {
      setBanner("Paiement réussi : la réservation a été mise à jour.");
      // Optionnel: nettoyer l'URL
      const url = new URL(window.location.href);
      url.searchParams.delete('paid');
      window.history.replaceState({}, '', url.toString());
    }
  }, [location.search]);

  const handleUpdate = async (id: string, statut: string) => {
    try {
      await updateReservation({ variables: { id, statut } });
      refetch();
    } catch (err) {
      console.error("Erreur de mise à jour :", err);
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await deleteReservation({ variables: { id } });
      refetch();
    } catch (err) {
      console.error("Erreur de suppression :", err);
    }
  };

  // ----- RÈGLE 72H : helper -----
  const canModify = (dateISO: string) => {
    const depart = new Date(`${dateISO}T00:00:00`);
    const diffHours = (depart.getTime() - Date.now()) / (1000 * 60 * 60);
    return diffHours >= 72;
  };

  const showTooLate = () => {
    alert("Modification impossible : il reste moins de 72 heures avant le départ.");
  };
  // ------------------------------

  if (loading) return <p className="text-center text-blue-700">Chargement...</p>;
  if (error) return <p className="text-center text-red-600">Erreur : {error.message}</p>;

  const reservations: Reservation[] = data?.reservations ?? [];

  return (
    <div className="min-h-screen bg-[#f9fbfd] text-gray-800">
      {/* NavBar */}
      <nav className="bg-blue-700 text-white py-4 px-8 shadow-md flex justify-between items-center">
        <h1 className="text-xl font-bold">🌍 Agence de Voyage</h1>
        <ul className="flex space-x-6 font-medium">
          <li>
            <Link to="/informations" className="hover:underline">
              Mes informations
            </Link>
          </li>
          <li>
            <Link to="/reservations" className="underline underline-offset-4">
              Mes réservations
            </Link>
          </li>
          <li>
            <Link to="/factures" className="hover:underline">
              Mes factures
            </Link>
          </li>
        </ul>
      </nav>

      {/* Bandeau succès */}
      {banner && (
        <div className="max-w-4xl mx-auto mt-6 px-4">
          <div className="flex items-center justify-between rounded-xl border border-green-200 bg-green-50 text-green-800 px-4 py-3">
            <span>{banner}</span>
            <button
              onClick={() => setBanner(null)}
              className="text-sm font-semibold hover:underline"
            >
              Fermer
            </button>
          </div>
        </div>
      )}

      {/* Titre */}
      <h2 className="text-3xl font-bold text-blue-800 text-center my-10">Mes réservations</h2>

      {/* Cartes */}
      <div className="space-y-10 max-w-4xl mx-auto px-4">
        {reservations.map((res: Reservation) => (
          <div key={res.id} className="bg-white rounded-2xl shadow-xl overflow-hidden">
            {/* Bandeau supérieur */}
            <div className="bg-blue-100 px-6 py-4 flex justify-between items-center">
              <h3 className="text-xl font-bold text-blue-900 flex items-center gap-2">
                ✈️ {res.destination}
              </h3>
              {res.statut === 'CONFIRMEE' && (
                <span className="text-sm font-semibold px-4 py-1 rounded-full bg-yellow-200 text-yellow-800">
                  CONFIRMÉE
                </span>
              )}
            </div>

            {/* Contenu */}
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 px-6 py-4 text-sm sm:text-base">
              <p><strong>Nom :</strong> {res.nom}</p>
              <p><strong>Prénom :</strong> {res.prenom}</p>
              <p><strong>Email :</strong> {res.email}</p>
              <p><strong>Date du voyage :</strong> {res.dateVoyage}</p>
              <p><strong>Nombre de personnes :</strong> {res.nombrePersonnes}</p>
              <p><strong>Date de réservation :</strong> {new Date(res.dateReservation).toLocaleString()}</p>
            </div>

            {/* Boutons */}
            <div className="flex justify-end gap-4 px-6 pb-6">
              {canModify(res.dateVoyage) ? (
                <button
                  onClick={() => navigate(`/reservation-edit/${res.id}`)}
                  className="bg-blue-100 text-blue-800 px-4 py-2 rounded-md hover:bg-blue-200 transition"
                >
                  Modifier
                </button>
              ) : (
                <button
                  onClick={showTooLate}
                  className="bg-gray-100 text-gray-400 px-4 py-2 rounded-md cursor-not-allowed"
                  title="Modification impossible : moins de 72h avant le départ"
                >
                  Modifier
                </button>
              )}

              <button
                onClick={() => handleDelete(res.id)}
                className="bg-red-100 text-red-800 px-4 py-2 rounded-md hover:bg-red-200 transition"
              >
                Annuler
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reservations;
