import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Définition du type attendu pour chaque facture
type Facture = {
  id: number;
  fichier_pdf: string;
  date_creation: string;
};

const Factures = () => {
  const [factures, setFactures] = useState<Facture[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchFactures = async () => {
      try {
        const response = await axios.get<Facture[]>('http://localhost:8000/api/factures/');

        console.log("Données reçues :", response.data);
        console.log("Type des données :", typeof response.data);
        console.log("Est-ce un tableau ?", Array.isArray(response.data));

        if (Array.isArray(response.data)) {
          setFactures(response.data);
        } else {
          throw new Error("Format de données inattendu : la réponse n'est pas un tableau.");
        }
      } catch (err) {
        console.error("Erreur lors du chargement des factures :", err);
        setError("Erreur lors du chargement des factures.");
      }
    };

    fetchFactures();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Mes factures</h1>

      {error && <p className="text-red-500">{error}</p>}

      {!error && factures.length === 0 && (
        <p>Aucune facture trouvée.</p>
      )}

      {Array.isArray(factures) && factures.length > 0 && (
        <ul className="space-y-4">
          {factures.map((facture) => (
            <li key={facture.id} className="border p-4 rounded shadow">
              <p>
                <strong>Date :</strong>{' '}
                {new Date(facture.date_creation).toLocaleDateString()}
              </p>
              <a
                href={`http://localhost:8000${facture.fichier_pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 underline"
              >
                Voir la facture (PDF)
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Factures;
