import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import ReservationForm from './components/ReservationForm';
import Reservations from './pages/Reservations';
import ReservationDetail from './pages/ReservationDetail';
import Factures from './pages/Factures';

function App() {
  return (
    <BrowserRouter>
      <div className="p-4">
        {/* Menu de navigation */}
        <nav className="mb-4 space-x-4">
          <Link to="/" className="text-blue-600 hover:underline">Faire une réservation</Link>
          <Link to="/reservations" className="text-blue-600 hover:underline">Mes réservations</Link>
          <Link to="/factures" className="text-blue-600 hover:underline">Mes factures</Link>
        </nav>

        {/* Les routes */}
        <Routes>
          <Route path="/" element={<ReservationForm />} />
          <Route path="/reservations" element={<Reservations />} />
          <Route path="/reservation/:id" element={<ReservationDetail />} />
          <Route path="/factures" element={<Factures />} />

        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
