import React from 'react';
import { useQuery, useMutation, gql } from '@apollo/client';
import { Link } from 'react-router-dom';

const GET_RESERVATIONS = gql`
  query {
    reservations {
      id
      nom
      prenom
      email
      destination
      dateVoyage
      nombrePersonnes
      statut
      dateReservation
    }
  }
`;

const UPDATE_RESERVATION = gql`
  mutation UpdateReservation($id: ID!, $statut: String) {
    updateReservation(id: $id, statut: $statut) {
      reservation {
        id
        statut
      }
    }
  }
`;

const DELETE_RESERVATION = gql`
  mutation DeleteReservation($id: ID!) {
    deleteReservation(id: $id) {
      ok
    }
  }
`;

interface Reservation {
  id: string;
  nom: string;
  prenom: string;
  email: string;
  destination: string;
  dateVoyage: string;
  nombrePersonnes: number;
  statut: string;
  dateReservation: string;
}

const Reservations = () => {
  const { loading, error, data, refetch } = useQuery(GET_RESERVATIONS);
  const [updateReservation] = useMutation(UPDATE_RESERVATION);
  const [deleteReservation] = useMutation(DELETE_RESERVATION);

  const handleUpdate = async (id: string, statut: string) => {
    try {
      await updateReservation({ variables: { id, statut } });
      refetch();
    } catch (err) {
      console.error("Erreur de mise √† jour :", err);
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await deleteReservation({ variables: { id } });
      refetch();
    } catch (err) {
      console.error("Erreur de suppression :", err);
    }
  };

  if (loading) return <p className="text-center text-blue-700">Chargement...</p>;
  if (error) return <p className="text-center text-red-600">Erreur : {error.message}</p>;

  return (
    <div className="min-h-screen bg-[#f9fbfd] text-gray-800">
      {/* NavBar */}
      <nav className="bg-blue-700 text-white py-4 px-8 shadow-md flex justify-between items-center">
        <h1 className="text-xl font-bold">üåç Agence de Voyage</h1>
        <ul className="flex space-x-6 font-medium">
          <li>
            <Link to="/informations" className="hover:underline">
              Mes informations
            </Link>
          </li>
          <li>
            <Link to="/reservations" className="underline underline-offset-4">
              Mes r√©servations
            </Link>
          </li>
          <li>
            <Link to="/factures" className="hover:underline">
              Mes factures
            </Link>
          </li>
        </ul>
      </nav>

      {/* Titre */}
      <h2 className="text-3xl font-bold text-blue-800 text-center my-10">Mes r√©servations</h2>

      {/* Cartes */}
      <div className="space-y-10 max-w-4xl mx-auto px-4">
        {data.reservations.map((res: Reservation) => (
          <div key={res.id} className="bg-white rounded-2xl shadow-xl overflow-hidden">
            {/* Bandeau sup√©rieur */}
            <div className="bg-blue-100 px-6 py-4 flex justify-between items-center">
              <h3 className="text-xl font-bold text-blue-900 flex items-center gap-2">
                ‚úàÔ∏è {res.destination}
              </h3>
              {res.statut === 'CONFIRMEE' && (
                <span className="text-sm font-semibold px-4 py-1 rounded-full bg-yellow-200 text-yellow-800">
                  CONFIRM√âE
                </span>
              )}
            </div>

            {/* Contenu */}
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 px-6 py-4 text-sm sm:text-base">
              <p><strong>Nom :</strong> {res.nom}</p>
              <p><strong>Pr√©nom :</strong> {res.prenom}</p>
              <p><strong>Email :</strong> {res.email}</p>
              <p><strong>Date du voyage :</strong> {res.dateVoyage}</p>
              <p><strong>Nombre de personnes :</strong> {res.nombrePersonnes}</p>
              <p><strong>Date de r√©servation :</strong> {new Date(res.dateReservation).toLocaleString()}</p>
            </div>

            {/* Boutons */}
            <div className="flex justify-end gap-4 px-6 pb-6">
              <button
                onClick={() => console.log('Modifier', res.id)}
                className="bg-blue-100 text-blue-800 px-4 py-2 rounded-md hover:bg-blue-200 transition"
              >
                Modifier
              </button>
              <button
                onClick={() => handleDelete(res.id)}
                className="bg-red-100 text-red-800 px-4 py-2 rounded-md hover:bg-red-200 transition"
              >
                Annuler
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reservations;
