import React, { useState } from 'react';
import { gql, useQuery, useMutation } from '@apollo/client';
import { useParams, useNavigate } from 'react-router-dom';

const GET_RESERVATION = gql`
  query GetReservation($id: ID!) {
    reservation(id: $id) {
      id
      nom
      prenom
      email
      destination
      dateVoyage
      nombrePersonnes
      statut
      dateReservation
    }
  }
`;

const DELETE_RESERVATION = gql`
  mutation DeleteReservation($id: ID!) {
    deleteReservation(id: $id) {
      ok
    }
  }
`;

const UPDATE_RESERVATION = gql`
  mutation UpdateReservation(
    $id: ID!
    $nom: String
    $prenom: String
    $email: String
    $nombrePersonnes: Int
    $statut: String
  ) {
    updateReservation(
      id: $id
      nom: $nom
      prenom: $prenom
      email: $email
      nombrePersonnes: $nombrePersonnes
      statut: $statut
    ) {
      reservation {
        id
        statut
      }
    }
  }
`;

const ReservationDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const { data, loading, error, refetch } = useQuery(GET_RESERVATION, {
    variables: { id },
  });

  const [deleteReservation] = useMutation(DELETE_RESERVATION);
  const [updateReservation] = useMutation(UPDATE_RESERVATION);

  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    nombrePersonnes: 1,
  });

  const handleDelete = async () => {
    try {
      await deleteReservation({ variables: { id } });
      navigate('/reservations');
    } catch (err) {
      console.error("Erreur lors de l‚Äôannulation :", err);
    }
  };

  const handleEdit = () => {
    const r = data.reservation;
    setFormData({
      nom: r.nom,
      prenom: r.prenom,
      email: r.email,
      nombrePersonnes: r.nombrePersonnes,
    });
    setIsEditing(true);
  };

  const handleSave = async () => {
    try {
      await updateReservation({ variables: { id, ...formData } });
      await refetch();
      setIsEditing(false);
    } catch (err) {
      console.error("Erreur de mise √† jour :", err);
    }
  };

  const handleConfirm = async () => {
    try {
      await updateReservation({
        variables: { id, statut: "CONFIRMEE" }
      });
      navigate(`/paiement/${id}`);
    } catch (err) {
      console.error("Erreur lors de la confirmation :", err);
    }
  };

  if (loading) return <p className="text-center text-blue-700 mt-10">Chargement...</p>;
  if (error) return <p className="text-center text-red-600 mt-10">Erreur : {error.message}</p>;
  if (!data || !data.reservation) {
    return <p className="text-center text-red-500 mt-10">Aucune r√©servation trouv√©e.</p>;
  }

  const res = data.reservation;

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-yellow-50">
      {/* MENU EN HAUT */}
      <header className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center shadow-md">
        <h1 className="text-xl font-bold">üåç Agence de Voyage</h1>
        <nav className="space-x-6">
          <a href="#" className="hover:underline">Mes informations</a>
          <a href="/reservations" className="underline font-semibold">Mes r√©servations</a>
          <a href="#" className="hover:underline">Mes factures</a>
        </nav>
      </header>

      {/* D√âTAIL DE LA R√âSERVATION */}
      <div className="max-w-2xl mx-auto bg-white shadow-xl rounded-2xl overflow-hidden mt-10">
        <div className="bg-blue-100 px-6 py-4 flex items-center space-x-2">
          <span className="text-xl">‚úàÔ∏è</span>
          <h2 className="text-xl font-bold text-blue-900">{res.destination}</h2>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 px-6 py-6 text-sm sm:text-base text-gray-800">
          {isEditing ? (
            <>
              <p>
                <strong>Nom :</strong><br />
                <input
                  type="text"
                  value={formData.nom}
                  onChange={(e) => setFormData({ ...formData, nom: e.target.value })}
                  className="border rounded px-2 py-1 w-full"
                />
              </p>
              <p>
                <strong>Pr√©nom :</strong><br />
                <input
                  type="text"
                  value={formData.prenom}
                  onChange={(e) => setFormData({ ...formData, prenom: e.target.value })}
                  className="border rounded px-2 py-1 w-full"
                />
              </p>
              <p>
                <strong>Email :</strong><br />
                <input
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  className="border rounded px-2 py-1 w-full"
                />
              </p>
              <p>
                <strong>Nombre de personnes :</strong><br />
                <input
                  type="number"
                  value={formData.nombrePersonnes}
                  min={1}
                  onChange={(e) =>
                    setFormData({
                      ...formData,
                      nombrePersonnes: parseInt(e.target.value) || 1,
                    })
                  }
                  className="border rounded px-2 py-1 w-full"
                />
              </p>
            </>
          ) : (
            <>
              <p><strong>Nom :</strong> {res.nom}</p>
              <p><strong>Pr√©nom :</strong> {res.prenom}</p>
              <p><strong>Email :</strong> {res.email}</p>
              <p><strong>Nombre de personnes :</strong> {res.nombrePersonnes}</p>
            </>
          )}
          <p><strong>Date du voyage :</strong> {res.dateVoyage}</p>
          <p><strong>Date de r√©servation :</strong> {new Date(res.dateReservation).toLocaleString()}</p>
        </div>

        {/* BOUTONS ACTION */}
        <div className="flex justify-center gap-4 px-6 pb-6 flex-wrap">
          <button
            className="bg-yellow-300 hover:bg-yellow-400 text-yellow-900 font-semibold px-4 py-2 rounded-md"
            onClick={handleConfirm}
          >
            Confirmer
          </button>
          <button
            onClick={handleDelete}
            className="bg-red-100 hover:bg-red-200 text-red-700 font-semibold px-4 py-2 rounded-md"
          >
            Annuler
          </button>

          {isEditing ? (
            <>
              <button
                onClick={handleSave}
                className="bg-green-100 hover:bg-green-200 text-green-700 font-semibold px-4 py-2 rounded-md"
              >
                Sauvegarder
              </button>
              <button
                onClick={() => setIsEditing(false)}
                className="bg-gray-100 hover:bg-gray-200 text-gray-700 font-semibold px-4 py-2 rounded-md"
              >
                Annuler la modification
              </button>
            </>
          ) : (
            <button
              onClick={handleEdit}
              className="bg-blue-100 hover:bg-blue-200 text-blue-700 font-semibold px-4 py-2 rounded-md"
            >
              Modifier
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReservationDetail;
