import React from 'react';
import { useParams } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe("pk_test_51R8CHoFaC4ABhVfvtcBNtizA9MT4Yl0DI6h3W3L3q0fW8nTqQsMPZqsabrr6imIXjPGzX88CngvGL7ismRjbXVpm001z2CWBTZ"); // ‚ö†Ô∏è Mets ta cl√© publique Stripe ici

const Paiement = () => {
  const { id } = useParams();

  const handlePaiement = async () => {
    const stripe = await stripePromise;

    // Appel √† ton backend pour cr√©er la session
    const response = await fetch("http://localhost:8000/create-checkout-session/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        reservation_id: id,
        amount: 50, // Montant en dollars
      }),
    });

    const session = await response.json();

    if (session.url) {
      window.location.href = session.url; // Redirection vers Stripe Checkout
    } else {
      console.error("Erreur de cr√©ation de session :", session);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-white via-blue-50 to-yellow-50">
      <div className="bg-white p-8 rounded-xl shadow-lg text-center">
        <h1 className="text-3xl font-bold text-blue-800 mb-4">üí≥ Paiement</h1>
        <p className="mb-6">Paiement pour la r√©servation <strong>#{id}</strong></p>
        <button
          onClick={handlePaiement}
          className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700"
        >
          Proc√©der au paiement
        </button>
      </div>
    </div>
  );
};

export default Paiement;
