// src/pages/Factures.tsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface Facture {
  id: number;
  fichier_pdf: string;
  date_creation: string;
  reservation: {
    id: number;
    destination: string;
    date_voyage: string;
  };
}

const Factures = () => {
  const [factures, setFactures] = useState<Facture[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchFactures = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://127.0.0.1:8000/api/factures/', {
          headers: {
            Authorization: `JWT ${token}`,
          },
        });
        setFactures(response.data);
      } catch (err) {
        setError("Erreur lors du chargement des factures.");
      } finally {
        setLoading(false);
      }
    };

    fetchFactures();
  }, []);

  if (loading) return <p className="text-center mt-10 text-blue-600">Chargement...</p>;
  if (error) return <p className="text-center mt-10 text-red-600">{error}</p>;

  return (
    <div className="min-h-screen bg-gray-50 py-10 px-6">
      <h2 className="text-3xl font-bold text-center text-blue-800 mb-10">ðŸ“„ Mes Factures</h2>
      <div className="max-w-3xl mx-auto space-y-6">
        {factures.map((facture) => (
          <div
            key={facture.id}
            className="bg-white shadow-lg rounded-xl p-6 flex justify-between items-center"
          >
            <div>
              <p className="font-semibold text-lg text-blue-700">
                RÃ©servation #{facture.reservation.id} - {facture.reservation.destination}
              </p>
              <p className="text-sm text-gray-500">
                Date voyage : {facture.reservation.date_voyage} <br />
                Date facture : {new Date(facture.date_creation).toLocaleDateString()}
              </p>
            </div>
            <a
              href={`http://127.0.0.1:8000/media/${facture.fichier_pdf}`}
              target="_blank"
              rel="noopener noreferrer"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition"
            >
              Voir PDF
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Factures;
