import React from 'react';
import { gql, useQuery } from '@apollo/client';
import { useParams, Link } from 'react-router-dom';

const GET_RESERVATION = gql`
  query GetReservation($id: ID!) {
    reservation(id: $id) {
      id
      nom
      prenom
      email
      destination
      dateVoyage
      nombrePersonnes
      statut
      dateReservation
    }
  }
`;

const ReservationDetail = () => {
  const { id } = useParams();
  const { data, loading, error } = useQuery(GET_RESERVATION, {
    variables: { id },
  });

  // üîç Debug logs
  console.log("loading", loading);
  console.log("error", error);
  console.log("data", data);

  if (loading) return <p className="text-center text-blue-700 mt-10">Chargement...</p>;
  if (error) return <p className="text-center text-red-600 mt-10">Erreur : {error.message}</p>;

  if (!data || !data.reservation) {
    return (
      <p className="text-center text-red-500 mt-10">
        Aucune r√©servation trouv√©e pour cet ID.
      </p>
    );
  }

  const res = data.reservation;

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-yellow-50 py-12 px-4">
      <div className="max-w-2xl mx-auto bg-white shadow-xl rounded-2xl overflow-hidden">
        <div className="bg-blue-100 px-6 py-4 flex justify-between items-center">
          <h2 className="text-xl font-bold text-blue-900">‚úàÔ∏è {res.destination}</h2>
          <span
            className={`text-sm font-semibold px-4 py-1 rounded-full ${
              res.statut === 'PAYEE'
                ? 'bg-green-200 text-green-800'
                : res.statut === 'CONFIRMEE'
                ? 'bg-yellow-200 text-yellow-800'
                : 'bg-red-200 text-red-800'
            }`}
          >
            {res.statut || 'EN ATTENTE'}
          </span>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 px-6 py-6 text-sm sm:text-base text-gray-800">
          <p><strong>Nom :</strong> {res.nom}</p>
          <p><strong>Pr√©nom :</strong> {res.prenom}</p>
          <p><strong>Email :</strong> {res.email}</p>
          <p><strong>Date du voyage :</strong> {res.dateVoyage}</p>
          <p><strong>Nombre de personnes :</strong> {res.nombrePersonnes}</p>
          <p><strong>Date de r√©servation :</strong> {new Date(res.dateReservation).toLocaleString()}</p>
        </div>

        <div className="text-center pb-6">
          <Link
            to="/reservations"
            className="inline-block mt-4 bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition"
          >
            Voir toutes mes r√©servations
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ReservationDetail;
