import React, { useState, useEffect, useMemo } from "react";
import { gql, useQuery, useMutation, useApolloClient } from "@apollo/client";
import { useParams, useNavigate, Link, useLocation } from "react-router-dom";

const GET_RESERVATION = gql`
  query GetReservation($id: ID!) {
    reservation(id: $id) {
      id
      nom
      prenom
      email
      destination
      dateVoyage
      nombrePersonnes
    }
  }
`;

const UPDATE_RESERVATION = gql`
  mutation UpdateReservation(
    $id: ID!
    $nom: String
    $prenom: String
    $email: String
    $nombrePersonnes: Int
  ) {
    updateReservation(
      id: $id
      nom: $nom
      prenom: $prenom
      email: $email
      nombrePersonnes: $nombrePersonnes
    ) {
      reservation { id }
    }
  }
`;

const ReservationEdit = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const client = useApolloClient();

  const { data, loading, error } = useQuery(GET_RESERVATION, {
    variables: { id: String(id) },
  });
  const [updateReservation] = useMutation(UPDATE_RESERVATION);

  const [formData, setFormData] = useState({
    nom: "",
    prenom: "",
    email: "",
    nombrePersonnes: 1,
  });
  const [originalNombre, setOriginalNombre] = useState<number | null>(null);

  useEffect(() => {
    if (data?.reservation) {
      const r = data.reservation;
      setFormData({
        nom: r.nom,
        prenom: r.prenom,
        email: r.email,
        nombrePersonnes: r.nombrePersonnes,
      });
      setOriginalNombre(r.nombrePersonnes);
    }
  }, [data]);

  // Rollback si retour avec ?payment=cancel (paiement Stripe annul√©)
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    if (params.get("payment") === "cancel" && id) {
      const optimisticId = sessionStorage.getItem("optimistic-reservation-id");
      const oldNb = sessionStorage.getItem("optimistic-old-nb");
      if (optimisticId === String(id) && oldNb) {
        try {
          const cacheId = client.cache.identify({ __typename: "ReservationType", id: String(id) });
          if (cacheId) {
            client.cache.modify({
              id: cacheId,
              fields: {
                nombrePersonnes() {
                  return parseInt(oldNb, 10);
                },
              },
            });
          }
        } catch { /* noop */ }
      }
      // nettoyage
      sessionStorage.removeItem("optimistic-reservation-id");
      sessionStorage.removeItem("optimistic-old-nb");
      sessionStorage.removeItem("optimistic-new-nb");
    }
  }, [location.search, id, client]);

  // delta > 0 => augmentation | delta < 0 => diminution
  const delta = useMemo(() => {
    if (originalNombre === null) return 0;
    return formData.nombrePersonnes - originalNombre;
  }, [formData.nombrePersonnes, originalNombre]);

  const handleSave = async () => {
    if (!id) return;

    // 1) Si augmentation: paiement Stripe (pas d'update serveur imm√©diat)
    if (delta > 0) {
      try {
        const resp = await fetch("http://127.0.0.1:8000/create-checkout-session/", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            reservation_id: id,
            new_nombre_personnes: formData.nombrePersonnes,
          }),
        });

        const payload = await resp.json();
        if (payload?.url) {
          // üîπ UI optimiste : on refl√®te tout de suite le nouveau nombre dans le cache
          try {
            const cacheId = client.cache.identify({ __typename: "ReservationType", id: String(id) });
            if (cacheId) {
              client.cache.modify({
                id: cacheId,
                fields: {
                  nombrePersonnes() {
                    return formData.nombrePersonnes;
                  },
                },
              });
            }
          } catch { /* noop */ }

          // on garde de quoi rollback si paiement annul√©
          sessionStorage.setItem("optimistic-reservation-id", String(id));
          sessionStorage.setItem("optimistic-old-nb", String(originalNombre ?? ""));
          sessionStorage.setItem("optimistic-new-nb", String(formData.nombrePersonnes));

          window.location.href = payload.url; // redirection Stripe
        } else {
          alert("Impossible d‚Äôouvrir le paiement Stripe (pas d‚ÄôURL retourn√©e).");
          console.error("Stripe payload:", payload);
        }
      } catch (e) {
        console.error("Erreur de redirection Stripe:", e);
        alert("Erreur lors de la redirection vers le paiement.");
      }
      return;
    }

    // 2) Si diminution ou inchang√©: on sauvegarde tout de suite c√¥t√© GraphQL
    try {
      await updateReservation({
        variables: {
          id: String(id),
          nom: formData.nom,
          prenom: formData.prenom,
          email: formData.email,
          nombrePersonnes: formData.nombrePersonnes,
        },
      });
      navigate("/reservations");
    } catch (err) {
      console.error("Erreur lors de la sauvegarde :", err);
      alert("Erreur lors de la sauvegarde");
    }
  };

  if (loading) return <p className="text-center mt-10 text-blue-700">Chargement‚Ä¶</p>;
  if (error) return <p className="text-center mt-10 text-red-600">Erreur : {error.message}</p>;

  return (
    <div className="min-h-screen bg-slate-50">
      <div className="max-w-5xl mx-auto px-4 pt-8">
        <div className="flex items-center justify-between mb-6">
          <Link to="/reservations" className="text-sm text-blue-700 hover:underline">
            ‚Üê Retour aux r√©servations
          </Link>
        </div>

        <div className="bg-white rounded-3xl shadow-xl overflow-hidden max-w-3xl mx-auto border border-blue-50">
          <div className="bg-blue-100 px-6 py-4 flex items-center justify-between">
            <h2 className="text-xl font-bold text-blue-900 flex items-center gap-2">
              ‚úèÔ∏è Modifier la r√©servation
            </h2>
            {data?.reservation?.destination && (
              <div className="text-sm font-semibold text-blue-900 opacity-80">
                ‚úàÔ∏è {data.reservation.destination} ¬∑ {data.reservation.dateVoyage}
              </div>
            )}
          </div>

          <div className="p-6 md:p-8">
            {/* Bandeau info delta */}
            {originalNombre !== null && delta !== 0 && (
              <div
                className={`mb-6 rounded-xl px-4 py-3 text-sm font-medium border ${
                  delta > 0
                    ? "bg-blue-50 text-blue-800 border-blue-200"
                    : "bg-green-50 text-green-800 border-green-200"
                }`}
              >
                {delta > 0
                  ? `Augmentation de ${delta} personne(s) ‚Äî un paiement suppl√©mentaire sera requis.`
                  : `Diminution de ${Math.abs(delta)} personne(s) ‚Äî un remboursement/avoir sera trait√©.`}
              </div>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <label className="block">
                <span className="block text-sm font-medium text-slate-700 mb-1">Nom</span>
                <input
                  type="text"
                  value={formData.nom}
                  onChange={(e) => setFormData({ ...formData, nom: e.target.value })}
                  className="w-full rounded-xl border border-slate-200 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-300"
                  placeholder="Nom"
                />
              </label>

              <label className="block">
                <span className="block text-sm font-medium text-slate-700 mb-1">Pr√©nom</span>
                <input
                  type="text"
                  value={formData.prenom}
                  onChange={(e) => setFormData({ ...formData, prenom: e.target.value })}
                  className="w-full rounded-xl border border-slate-200 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-300"
                  placeholder="Pr√©nom"
                />
              </label>

              <label className="block md:col-span-2">
                <span className="block text-sm font-medium text-slate-700 mb-1">Email</span>
                <input
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  className="w-full rounded-xl border border-slate-200 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-300"
                  placeholder="exemple@mail.com"
                />
              </label>

              <label className="block md:col-span-2">
                <span className="block text-sm font-medium text-slate-700 mb-1">Nombre de personnes</span>
                <input
                  type="number"
                  min={1}
                  step={1}
                  onWheel={(e) => e.currentTarget.blur()}  // emp√™che l‚Äôauto-incr√©ment au scroll
                  value={formData.nombrePersonnes}
                  onChange={(e) => {
                    const v = Math.max(1, parseInt(e.target.value || "1"));
                    setFormData({ ...formData, nombrePersonnes: v });
                  }}
                  className="w-full max-w-xs rounded-xl border border-slate-200 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-300"
                />
                {originalNombre !== null && (
                  <p className="mt-1 text-xs text-slate-500">
                    Nombre initial : {originalNombre} ‚Äî Changement : {delta > 0 ? `+${delta}` : delta}
                  </p>
                )}
              </label>
            </div>

            <div className="mt-8 flex justify-end gap-3">
              <button
                onClick={() => navigate("/reservations")}
                className="rounded-2xl px-5 py-2 bg-slate-100 text-slate-800 hover:bg-slate-200 focus:outline-none focus:ring-2 focus:ring-slate-300"
              >
                Annuler
              </button>
              <button
                onClick={handleSave}
                className="rounded-2xl px-5 py-2 bg-blue-600 text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-300 shadow-sm"
                title={delta > 0 ? "Vous serez redirig√© vers le paiement Stripe" : "Sauvegarder la modification"}
              >
                {delta > 0 ? "Payer le suppl√©ment" : "Sauvegarder"}
              </button>
            </div>
          </div>
        </div>

        <p className="text-center text-xs text-slate-500 mt-6">
          Les augmentations redirigent vers Stripe. Les diminutions seront rembours√©es lors de la prochaine √©tape (backend).
        </p>
      </div>
    </div>
  );
};

