import React, { useState, useEffect } from "react";
import { gql, useQuery, useMutation } from "@apollo/client";
import { useParams, useNavigate } from "react-router-dom";

const GET_RESERVATION = gql`
  query GetReservation($id: ID!) {
    reservation(id: $id) {
      id
      nom
      prenom
      email
      destination
      dateVoyage
      nombrePersonnes
    }
  }
`;

const UPDATE_RESERVATION = gql`
  mutation UpdateReservation(
    $id: ID!
    $nom: String
    $prenom: String
    $email: String
    $nombrePersonnes: Int
  ) {
    updateReservation(
      id: $id
      nom: $nom
      prenom: $prenom
      email: $email
      nombrePersonnes: $nombrePersonnes
    ) {
      reservation {
        id
        nom
        prenom
        email
        nombrePersonnes
      }
    }
  }
`;

const ReservationEdit = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const { data, loading, error } = useQuery(GET_RESERVATION, {
    variables: { id: String(id) },
  });

  const [updateReservation] = useMutation(UPDATE_RESERVATION);

  const [formData, setFormData] = useState({
    nom: "",
    prenom: "",
    email: "",
    nombrePersonnes: 1,
  });

  useEffect(() => {
    if (data?.reservation) {
      const r = data.reservation;
      setFormData({
        nom: r.nom,
        prenom: r.prenom,
        email: r.email,
        nombrePersonnes: r.nombrePersonnes,
      });
    }
  }, [data]);

  const handleSave = async () => {
    try {
      await updateReservation({
        variables: {
          id: String(id),
          nom: formData.nom,
          prenom: formData.prenom,
          email: formData.email,
          nombrePersonnes: formData.nombrePersonnes,
        },
      });
      navigate("/reservations");
    } catch (err) {
      console.error("Erreur lors de la sauvegarde :", err);
      alert("Erreur lors de la sauvegarde");
    }
  };

  if (loading) return <p className="text-center mt-10">Chargement...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">Erreur : {error.message}</p>;

  return (
    <div className="min-h-screen bg-[#f9fbfd] flex justify-center items-center p-4">
      <div className="bg-white shadow-xl rounded-2xl max-w-lg w-full p-6">
        <h2 className="text-2xl font-bold text-blue-800 mb-6">Modifier la réservation</h2>

        <label className="block mb-4">
          <span className="font-semibold">Nom :</span>
          <input
            type="text"
            value={formData.nom}
            onChange={(e) => setFormData({ ...formData, nom: e.target.value })}
            className="border rounded px-2 py-1 w-full"
          />
        </label>

        <label className="block mb-4">
          <span className="font-semibold">Prénom :</span>
          <input
            type="text"
            value={formData.prenom}
            onChange={(e) => setFormData({ ...formData, prenom: e.target.value })}
            className="border rounded px-2 py-1 w-full"
          />
        </label>

        <label className="block mb-4">
          <span className="font-semibold">Email :</span>
          <input
            type="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            className="border rounded px-2 py-1 w-full"
          />
        </label>

        <label className="block mb-6">
          <span className="font-semibold">Nombre de personnes :</span>
          <input
            type="number"
            min={1}
            value={formData.nombrePersonnes}
            onChange={(e) =>
              setFormData({
                ...formData,
                nombrePersonnes: parseInt(e.target.value) || 1,
              })
            }
            className="border rounded px-2 py-1 w-full"
          />
        </label>

        <div className="flex justify-end gap-4">
          <button
            onClick={() => navigate("/reservations")}
            className="bg-gray-100 hover:bg-gray-200 text-gray-700 px-4 py-2 rounded-md"
          >
            Annuler
          </button>
          <button
            onClick={handleSave}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md"
          >
            Sauvegarder
          </button>
        </div>
      </div>
    </div>
  );
};

export default ReservationEdit;
