// src/pages/Paiement.tsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY!);

const Paiement = () => {
  const { id } = useParams();
  const [message, setMessage] = useState('PrÃ©paration du paiementâ€¦');

  useEffect(() => {
    console.log('âž¡ï¸ Lancement crÃ©ation session Stripe pour rÃ©servation', id);
    const createSession = async () => {
      try {
        const response = await fetch('http://localhost:8000/create-checkout-session/', {
          method: 'POST',
          credentials: 'include',              // <-- indispensable pour passer le cookie de session
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ reservation_id: id }),
        });
        console.log('â† Status /create-checkout-session :', response.status);
        if (!response.ok) {
          const text = await response.text();
          console.error('âŒ Erreur serveur Paiement:', text);
          setMessage(`Erreur serveur : ${text}`);
          return;
        }

        const { url } = await response.json();
        console.log('ðŸ”— Redirection vers Stripe Checkout :', url);
        window.location.href = url;
      } catch (err: any) {
        console.error('âŒ Erreur fetch Paiement :', err);
        setMessage(`Erreur : ${err.message}`);
      }
    };
    createSession();
  }, [id]);

  return (
    <div className="flex items-center justify-center h-screen">
      <p className="text-lg">{message}</p>
    </div>
  );
};

export default Paiement;
