// src/pages/Paiement.tsx
import React, { useEffect, useState } from 'react';
import { useParams }              from 'react-router-dom';
import { loadStripe }             from '@stripe/stripe-js';

const stripePromise = loadStripe('pk_test_XXXXXXXXXXXXXXXXXXXXXXXX');

export default function Paiement() {
  const { id } = useParams();
  const [msg, setMsg] = useState('Préparation du paiement…');

  useEffect(() => {
    (async () => {
      try {
        const res = await fetch('http://192.168.2.15:8000/create-checkout-session/', {
          method: 'POST',
          headers: {'Content-Type':'application/json'},
          body: JSON.stringify({ reservation_id: id }),
        });
        if (!res.ok) throw new Error(await res.text());
        const { url } = await res.json();
        window.location.href = url;
      } catch (e: any) {
        setMsg(`Erreur : ${e.message}`);
      }
    })();
  }, [id]);

  return <div className="h-screen flex items-center justify-center"><p>{msg}</p></div>;
}
