// src/pages/Paiement.tsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

export default function Paiement() {
  const { id } = useParams<{ id: string }>();
  const [msg, setMsg] = useState('Préparation du paiement…');

  useEffect(() => {
    if (!id) {
      setMsg("ID de réservation manquant");
      return;
    }

    (async () => {
      try {
        const res = await fetch('http://192.168.2.15:8000/create-checkout-session/', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ reservation_id: id }),
        });

        if (!res.ok) throw new Error(await res.text());

        const { url } = await res.json();
        if (!url) throw new Error("URL de paiement introuvable");

        // Redirection vers Stripe Checkout
        window.location.href = url;
      } catch (e: any) {
        setMsg(`Erreur : ${e.message}`);
      }
    })();
  }, [id]);

  return (
    <div className="h-screen flex items-center justify-center">
      <p className="text-lg">{msg}</p>
    </div>
  );
}
