import React, { useEffect, useState } from "react";
import axios from "axios";

// DÃ©finition du type attendu pour chaque facture
type Facture = {
  id: number;
  fichier_pdf: string;
  date_creation: string;
};

const Factures = () => {
  const [factures, setFactures] = useState<Facture[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchFactures = async () => {
      try {
        const response = await axios.get<Facture[]>("http://localhost:8000/api/factures/");
        if (Array.isArray(response.data)) {
          setFactures(response.data);
        } else {
          throw new Error("Format de donnÃ©es inattendu : la rÃ©ponse n'est pas un tableau.");
        }
      } catch (err) {
        console.error("Erreur lors du chargement des factures :", err);
        setError("Erreur lors du chargement des factures.");
      }
    };

    fetchFactures();
  }, []);

  const formatDate = (iso: string) =>
    new Date(iso).toLocaleDateString(undefined, { year: "numeric", month: "numeric", day: "numeric" });

  return (
    <div className="min-h-screen bg-slate-50">
      <div className="max-w-5xl mx-auto px-4 py-10">
        <h1 className="text-4xl font-extrabold text-center text-blue-700 mb-10">Mes factures</h1>

        {error && (
          <div className="rounded-xl bg-red-50 border border-red-100 text-red-700 px-4 py-3 mb-6">
            {error}
          </div>
        )}

        {!error && factures.length === 0 && (
          <div className="rounded-2xl bg-white shadow-lg border border-slate-100 p-6 text-slate-600">
            Aucune facture trouvÃ©e.
          </div>
        )}

        <ul className="space-y-8">
          {factures.map((facture) => (
            <li
              key={facture.id}
              className="bg-white rounded-3xl shadow-xl border border-blue-50 overflow-hidden"
            >
              {/* En-tÃªte de la carte */}
              <div className="bg-blue-100 text-blue-900 px-6 py-4 flex items-center gap-2">
                <span className="text-xl">ðŸ“„</span>
                <h2 className="text-xl font-semibold">Facture #{facture.id}</h2>
              </div>

              {/* Contenu de la carte */}
              <div className="p-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-y-2 gap-x-8 text-slate-800">
                  <p>
                    <span className="font-semibold">Date :</span>{" "}
                    {formatDate(facture.date_creation)}
                  </p>
                  <p className="md:text-right">
                    <span className="font-semibold">Fichier :</span>{" "}
                    PDF
                  </p>
                </div>

                {/* Actions */}
                <div className="mt-6 flex flex-wrap gap-3 md:justify-end">
                  <a
                    href={`http://localhost:8000${facture.fichier_pdf}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-flex items-center justify-center rounded-2xl px-5 py-2 text-sm font-medium bg-blue-600 text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-300 shadow-sm"
                  >
                    Voir le PDF
                  </a>
                  <a
                    href={`http://localhost:8000${facture.fichier_pdf}`}
                    download
                    className="inline-flex items-center justify-center rounded-2xl px-5 py-2 text-sm font-medium bg-slate-100 text-slate-800 hover:bg-slate-200 focus:outline-none focus:ring-2 focus:ring-slate-300 shadow-sm"
                  >
                    TÃ©lÃ©charger
                  </a>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Factures;
