import React, { useState } from 'react';

export default function ReservationForm() {
  const [formData, setFormData] = useState({
    nom: '',
    email: '',
    date: '',
    nombrePersonnes: 1,
  });

  const [errors, setErrors] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const validate = () => {
    const newErrors = {};
    if (!formData.nom.trim()) newErrors.nom = 'Le nom est obligatoire';
    if (!formData.email.includes('@')) newErrors.email = "L'email est invalide";
    if (!formData.date) newErrors.date = 'La date est obligatoire';
    if (formData.nombrePersonnes < 1) newErrors.nombrePersonnes = 'Au moins 1 personne';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: name === 'nombrePersonnes' ? parseInt(value) : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      setSubmitted(true);
      // ici tu peux appeler ton API ou gérer la logique de soumission
    }
  };

  if (submitted) {
    return (
      <div className="max-w-md mx-auto p-6 bg-white rounded-xl shadow-md text-center">
        <h2 className="text-2xl font-bold text-green-600 mb-4">Réservation prise en compte !</h2>
        <p>Merci {formData.nom} pour votre réservation.</p>
      </div>
    );
  }

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-6 bg-white rounded-xl shadow-md space-y-6">
      <h1 className="text-3xl font-extrabold text-blue-900 mb-6 text-center">Réserver votre voyage</h1>

      <div>
        <label htmlFor="nom" className="block text-blue-800 font-semibold mb-1">Nom complet</label>
        <input
          type="text"
          id="nom"
          name="nom"
          value={formData.nom}
          onChange={handleChange}
          className={`w-full border rounded-md p-3 text-lg ${
            errors.nom ? 'border-red-500' : 'border-gray-300'
          } focus:outline-none focus:ring-2 focus:ring-blue-500`}
          placeholder="Votre nom complet"
        />
        {errors.nom && <p className="text-red-500 text-sm mt-1">{errors.nom}</p>}
      </div>

      <div>
        <label htmlFor="email" className="block text-blue-800 font-semibold mb-1">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className={`w-full border rounded-md p-3 text-lg ${
            errors.email ? 'border-red-500' : 'border-gray-300'
          } focus:outline-none focus:ring-2 focus:ring-blue-500`}
          placeholder="exemple@mail.com"
        />
        {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
      </div>

      <div>
        <label htmlFor="date" className="block text-blue-800 font-semibold mb-1">Date du voyage</label>
        <input
          type="date"
          id="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          className={`w-full border rounded-md p-3 text-lg ${
            errors.date ? 'border-red-500' : 'border-gray-300'
          } focus:outline-none focus:ring-2 focus:ring-blue-500`}
        />
        {errors.date && <p className="text-red-500 text-sm mt-1">{errors.date}</p>}
      </div>

      <div>
        <label htmlFor="nombrePersonnes" className="block text-blue-800 font-semibold mb-1">Nombre de personnes</label>
        <input
          type="number"
          id="nombrePersonnes"
          name="nombrePersonnes"
          min="1"
          value={formData.nombrePersonnes}
          onChange={handleChange}
          className={`w-full border rounded-md p-3 text-lg ${
            errors.nombrePersonnes ? 'border-red-500' : 'border-gray-300'
          } focus:outline-none focus:ring-2 focus:ring-blue-500`}
        />
        {errors.nombrePersonnes && <p className="text-red-500 text-sm mt-1">{errors.nombrePersonnes}</p>}
      </div>

      <button
        type="submit"
        className="w-full bg-yellow-400 hover:bg-yellow-500 transition-colors text-blue-900 font-bold py-3 rounded-xl shadow-md"
      >
        Réserver
      </button>
    </form>
  );
}
